[h:'<!-- --------------------------------- BOT_message ------------------------------------------------>']
<!-- 
0:message, 
OPTIONAL
1:headertxt, 
2:color (bgcolor-txtcolor), 
3:userList, 
4:token, 
5:target,
6:broadcast, 
7:width of message table
8:token only, dont show the name just the image
-->

<!-- ignore output , new scope -->

<!-- arguments -->
[h:message		= arg(0)]

<!-- Optional arguments -->
[h,if(argCount() > 1): headerText	= arg(1) ; headerText	= ""]
[h, if (indexOf(headerText, ":width=") != -1), CODE:{
	<!-- this will get overwritten by argument 7 should that argument be used.  -->
	[break		= indexOf(headerText, ":width=")]
	[width		= substring(headerText, break + 7)]
	[headerText	= substring(headerText, 0, break)]
};{}]

[h,if(argCount() > 2): color 		= arg(2) ; color		= "black"]
[h,if(indexOf(color, "-")+1), CODE:{
	[break		= indexOf(color, "-")]
	[bgColor	= substring(color, 0, break)]
	[textColor	= substring(color, break + 1)]
}; {
	[bgColor	= color]
	[textColor	= "white"]
}]

[h,if(argCount() > 3): userList		= arg(3) ; userList		= "[]"]

[h,if(argCount() > 4 ), CODE:{
