	}]
}; {
	[target			= ""]
	[targetImage	= ""]
}] 

[h,if(argCount() > 6): bc			= arg(6) ; bc		= 1]

[h,if(argCount() > 7): width		= arg(7) ; width	= "100%"]

[h,if(argCount() > 8): me = if(arg(8), "", me)] <!-- token only, dont show the name just the image -->

<!-- the actual code -->
