[overlay("fx.Ambience", "zorder=-2"): {

[r: strformat('

<html>
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1">
<style>
%{css}
</style>
</head>
<body>
<div class="falling-leaves"></div>
<script>
const leaftypes = ["leaf1", "leaf2", "leaf3"];
var LeafScene = function(el) {
    this.viewport = el;
    this.world = document.createElement("div");
    this.leaves = [];

    this.options = {
      numLeaves: 20,
      wind: {
        magnitude: 1.2,
        maxSpeed: 12,
        duration: 300,
        start: 0,
        speed: 0
      },
    };

    this.width = this.viewport.offsetWidth;
    this.height = this.viewport.offsetHeight;

    // animation helper
    this.timer = 0;

    this._resetLeaf = function(leaf) {

      // place leaf towards the top left
      leaf.x = this.width * 2 - Math.random()*this.width*1.75;
      leaf.y = -10;
      leaf.z = Math.random()*200;
      if (leaf.x > this.width) {
        leaf.x = this.width + 10;
        leaf.y = Math.random()*this.height/2;
      }
      // at the start, the leaf can be anywhere
      if (this.timer == 0) {
        leaf.y = Math.random()*this.height;
      }

      // Choose axis of rotation.
      // If axis is not X, chose a random static x-rotation for greater variability
      leaf.rotation.speed = Math.random()*10;
      var randomAxis = Math.random();
      if (randomAxis > 0.5) {
        leaf.rotation.axis = "X";
      } else if (randomAxis > 0.25) {
        leaf.rotation.axis = "Y";
        leaf.rotation.x = Math.random()*180 + 90;
      } else {
        leaf.rotation.axis = "Z";
        leaf.rotation.x = Math.random()*360 - 180;
        // looks weird if the rotation is too fast around this axis
        leaf.rotation.speed = Math.random()*3;
      }

      // random speed
      leaf.xSpeedVariation = Math.random() * 0.8 - 0.4;
      leaf.ySpeed = Math.random() + 1.5;

      return leaf;
    }

    this._updateLeaf = function(leaf) {
      var leafWindSpeed = this.options.wind.speed(this.timer - this.options.wind.start, leaf.y);

      var xSpeed = leafWindSpeed + leaf.xSpeedVariation;
      leaf.x -= xSpeed;
      leaf.y += leaf.ySpeed;
      leaf.rotation.value += leaf.rotation.speed;

      var t = "translateX( " + leaf.x + "px ) translateY( " + leaf.y + "px ) translateZ( " + leaf.z + "px )  rotate" + leaf.rotation.axis + "( " + leaf.rotation.value + "deg )";
      if (leaf.rotation.axis !== "X") {
        t += " rotateX(" + leaf.rotation.x + "deg)";
      }
      leaf.el.style.webkitTransform = t;
      leaf.el.style.MozTransform = t;
      leaf.el.style.oTransform = t;
      leaf.el.style.transform = t;

      // reset if out of view
      if (leaf.x < -10 || leaf.y > this.height + 10) {
        this._resetLeaf(leaf);
      }
    }

    this._updateWind = function() {
      // wind follows a sine curve: asin(b*time + c) + a
      // where a = wind magnitude as a function of leaf position, b = wind.duration, c = offset
      // wind duration should be related to wind magnitude, e.g. higher windspeed means longer gust duration

      if (this.timer === 0 || this.timer > (this.options.wind.start + this.options.wind.duration)) {

        this.options.wind.magnitude = Math.random() * this.options.wind.maxSpeed;
        this.options.wind.duration = this.options.wind.magnitude * 50 + (Math.random() * 20 - 10);
        this.options.wind.start = this.timer;

        var screenHeight = this.height;

        this.options.wind.speed = function(t, y) {
          // should go from full wind speed at the top, to 1/2 speed at the bottom, using leaf Y
          var a = this.magnitude/2 * (screenHeight - 2*y/3)/screenHeight;
          return a * Math.sin(2*Math.PI/this.duration * t + (3 * Math.PI/2)) + a;
        }
      }
    }
  }

  LeafScene.prototype.init = function() {

    for (var i = 0; i < this.options.numLeaves; i++) {
      var leaf = {
        el: document.createElement("div"),
        x: 0,
        y: 0,
        z: 0,
        rotation: {
          axis: "X",
          value: 0,
          speed: 0,
          x: 0
        },
        xSpeedVariation: 0,
        ySpeed: 0,
        path: {
          type: 1,
          start: 0,

        },
        image: 1
      };
      leaf.el.classList.add(leaftypes[Math.floor(Math.random() * leaftypes.length)]);
      this._resetLeaf(leaf);
      this.leaves.push(leaf);
      this.world.appendChild(leaf.el);
    }

    this.world.className = "leaf-scene";
    this.viewport.appendChild(this.world);

    // set perspective
    this.world.style.webkitPerspective = "400px";
    this.world.style.MozPerspective = "400px";
    this.world.style.oPerspective = "400px";
    this.world.style.perspective = "400px";

    // reset window height/width on resize
    var self = this;
    window.onresize = function(event) {
      self.width = self.viewport.offsetWidth;
      self.height = self.viewport.offsetHeight;
    };
  }

  LeafScene.prototype.render = function() {
    this._updateWind();
    for (var i = 0; i < this.leaves.length; i++) {
      this._updateLeaf(this.leaves[i]);
    }

    this.timer++;

    requestAnimationFrame(this.render.bind(this));
  }

  // start up leaf scene
  var leafContainer = document.querySelector(".falling-leaves"),
      leaves = new LeafScene(leafContainer);

  leaves.init();
  leaves.render();
</script>
</body>
</html>
')]
}]

[overlay("fx.Weather", "zorder=-1"): {
	[r: '
<html>
<head>
<meta charset="UTF-8">
<style>
:root {
	--color1: rgba(34,34,34,.6);
	--color2: rgba(59,59,59,.3);
}
html {box-sizing: border-box;}

*,
*:before,
*:after {
  box-sizing: inherit;
  margin: 0;
  padding: 0;
}

html,
body,
canvas {
  height: 100vh;
  width: 100vw;
}

body {
  position: relative;
  z-index: 0;
  &:after {
    content: "";
    height: 100vh;
  width: 100vw;
    display: block;
  position: absolute;
  top: 0;
  left: 0;
    background-color: rgba(0, 0, 0, 0.1);
    z-index: 1;
    animation: thunder-bg 6s infinite;
  }
}

canvas {
  display: block;
  position: absolute;
  top: 0;
  left: 0;
}

#tsCanvas3 {z-index: 5;}
#tsCanvas2 {z-index: 10;}
#tsCanvas1 {z-index: 100;}

.thunder {animation: thunder-bg 6s infinite;}

@keyframes thunder-bg {
	0%   { background-color: var(--color1); }
	9%   { background-color: var(--color1); }
	10%  { background-color: var(--color2); }
	10.5%{ background-color: var(--color1); }
	80%  { background-color: var(--color1); }
	82%  { background-color: var(--color2); }
	83%  { background-color: var(--color1); }
	83.5%{ background-color: var(--color2); }
	100% { background-color: var(--color1); }
}
</style>
</head>
<body class="thunder">
  <canvas id="tsCanvas1"></canvas>
  <canvas id="tsCanvas2"></canvas>
  <canvas id="tsCanvas3"></canvas>
</body>
<script type="text/javascript">

var tsCanvas1 = document.getElementById("tsCanvas1");
var tsCanvas2 = document.getElementById("tsCanvas2");
var tsCanvas3 = document.getElementById("tsCanvas3");
var tsCtx1 = tsCanvas1.getContext("2d");
var tsCtx2 = tsCanvas2.getContext("2d");
var tsCtx3 = tsCanvas3.getContext("2d");

var rainthroughnum = 350;
var speedRainTrough = 25;
var RainTrough = [];

var rainnum = 60;
var rain = [];

var lightning = [];
var lightTimeCurrent = 0;
var lightTimeTotal = 0;

var w = tsCanvas1.width = tsCanvas2.width = tsCanvas3.width = window.innerWidth;
var h = tsCanvas1.height = tsCanvas2.height = tsCanvas3.height = window.innerHeight;
window.addEventListener("resize", function() {
  w = tsCanvas1.width = tsCanvas2.width = tsCanvas3.width = window.innerWidth;
  h = tsCanvas1.height = tsCanvas2.height = tsCanvas3.height = window.innerHeight;
});

function random(min, max) {
  return Math.random() * (max - min + 1) + min;
}

function cleartsCanvas1() {
  tsCtx1.clearRect(0, 0, w, h);
}

function cleartsCanvas2() {
  tsCtx2.clearRect(0, 0, tsCanvas2.width, tsCanvas2.height);
}

function cleartsCanvas3() {
  tsCtx3.globalCompositeOperation = "destination-out";
  tsCtx3.fillStyle = "rgba(0,0,0," + random(1, 30) / 100 + ")";
  tsCtx3.fillRect(0, 0, w, h);
  tsCtx3.globalCompositeOperation = "source-over";
};

function createRainTrough() {
  for (var i = 0; i < rainthroughnum; i++) {
    RainTrough[i] = {
      x: random(0, w),
      y: random(0, h),
      length: Math.floor(random(1, 830)),
      opacity: Math.random() * 0.2,
      xs: random(-2, 2),
      ys: random(10, 20)
    };
  }
}

function createRain() {
  for (var i = 0; i < rainnum; i++) {
    rain[i] = {
      x: Math.random() * w,
      y: Math.random() * h,
      l: Math.random() * 1,
      xs: -4 + Math.random() * 4 + 2,
      ys: Math.random() * 10 + 10
    };
  }
}

function createLightning() {
  var x = random(100, w - 100);
  var y = random(0, h / 4);

  var createCount = random(1, 3);
  for (var i = 0; i < createCount; i++) {
    single = {
      x: x,
      y: y,
      xRange: random(5, 30),
      yRange: random(10, 25),
      path: [{
        x: x,
        y: y
      }],
      pathLimit: random(40, 55)
    };
    lightning.push(single);
  }
};

function drawRainTrough(i) {
  tsCtx1.beginPath();
  var grd = tsCtx1.createLinearGradient(0, RainTrough[i].y, 0, RainTrough[i].y + RainTrough[i].length);
  grd.addColorStop(0, "rgba(255,255,255,0)");
  grd.addColorStop(1, "rgba(255,255,255," + RainTrough[i].opacity + ")");

  tsCtx1.fillStyle = grd;
  tsCtx1.fillRect(RainTrough[i].x, RainTrough[i].y, 1, RainTrough[i].length);
  tsCtx1.fill();
}

function drawRain(i) {
  tsCtx2.beginPath();
  tsCtx2.moveTo(rain[i].x, rain[i].y);
  tsCtx2.lineTo(rain[i].x + rain[i].l * rain[i].xs, rain[i].y + rain[i].l * rain[i].ys);
  tsCtx2.strokeStyle = "rgba(174,194,224,0.5)";
  tsCtx2.lineWidth = 1;
  tsCtx2.lineCap = "round";
  tsCtx2.stroke();
}

function drawLightning() {
  for (var i = 0; i < lightning.length; i++) {
    var light = lightning[i];

    light.path.push({
      x: light.path[light.path.length - 1].x + (random(0, light.xRange) - (light.xRange / 2)),
      y: light.path[light.path.length - 1].y + (random(0, light.yRange))
    });

    if (light.path.length > light.pathLimit) {
      lightning.splice(i, 1);
    }

    tsCtx3.strokeStyle = "rgba(255, 255, 255, .1)";
    tsCtx3.lineWidth = 3;
    if (random(0, 15) === 0) {
      tsCtx3.lineWidth = 6;
    }
    if (random(0, 30) === 0) {
      tsCtx3.lineWidth = 8;
    }

    tsCtx3.beginPath();
    tsCtx3.moveTo(light.x, light.y);
    for (var pc = 0; pc < light.path.length; pc++) {
      tsCtx3.lineTo(light.path[pc].x, light.path[pc].y);
    }
    if (Math.floor(random(0, 30)) === 1) { //to fos apo piso
      tsCtx3.fillStyle = "rgba(255, 255, 255, " + random(1, 3) / 100 + ")";
      tsCtx3.fillRect(0, 0, w, h);
    }
    tsCtx3.lineJoin = "miter";
    tsCtx3.stroke();
  }
};

function animateRainTrough() {
	cleartsCanvas1();
	for (var i = 0; i < rainthroughnum; i++) {
		if (RainTrough[i].y >= h) {
			RainTrough[i].y = h - RainTrough[i].y - RainTrough[i].length * 5;
		} else {
			RainTrough[i].y += speedRainTrough;
		}
		drawRainTrough(i);
	}
}

function animateRain() {
	cleartsCanvas2();
	for (var i = 0; i < rainnum; i++) {
		rain[i].x += rain[i].xs;
		rain[i].y += rain[i].ys;
		if (rain[i].x > w || rain[i].y > h) {
			rain[i].x = Math.random() * w;
			rain[i].y = -20;
		}
		drawRain(i);
	}
}

function animateLightning() {
	cleartsCanvas3();
	lightTimeCurrent++;
	if (lightTimeCurrent >= lightTimeTotal) {
		createLightning();
		lightTimeCurrent = 0;
		lightTimeTotal = 200;  //rand(100, 200)
	}
	drawLightning();
}

function init() {
	createRainTrough();
	createRain();
	window.addEventListener("resize", createRainTrough);
}
init();

function animloop() {
	animateRainTrough();
	animateRain();
	animateLightning();
	requestAnimationFrame(animloop);
}
animloop();
</script>
</html>
' ]
}]

