};		

function Forces(){}
Forces.zeroForce = function() {	return (new Vector3D(0,0,0));}
Forces.forceField = function(q,E) {	return E.multiply(q);}
Forces.add = function(arr){
		var forceSum = new Vector3D(0,0,0);
