function getNewBoid() {
  var retVal = {
	position: {x: Math.random(), y: Math.random(), z: Math.random()}, 
	speed: [
		{x: Math.sign(Math.random()-0.5)*0.1+Math.random()*0.9, 
		y: Math.sign(Math.random()-0.5)*0.1+Math.random()*0.9, z: 0}, 
		{x: Math.random()-0.5, y: Math.random()-0.5, z: 0}], 
