  for (i = 0; i < simulationInfo.boids.length; ++i) {
    avoid = {x: 0, y: 0, z: 0};
    if(i%(Math.floor(Math.random()*12))==0){align = {x: (Math.random()-0.6)/30, y: (Math.random()-0.4)/30, z: (Math.random()-0.5)/50}} else {align = {x: 0, y: 0, z: 0}};
