<!DOCTYPE HTML>
<html>
<head>
<meta charset="UTF-8">
<title>Canvas Snow Effect</title>
<style type="text/css">
%{css}
</style>
<body>
<canvas id="scene"></canvas>
<script type="text/javascript">
"use strict";

var FastRandom = function()
{
    var me = this;
    var stack = [];
    var index = 0;
    var max   = 1000;

    this.nextFloat = function()    {
        if (index === max) {
            index = parseInt(Math.random() * (max - 1));
        }
        return stack[index++];
    };

    this.nextRealFloat = function()    {
        return Math.random();
    };

    this.nextInt = function(min, max)    {
        return parseInt((max - min) * me.nextFloat() + min);
    };

    this.nextRealInt = function(min, max)    {
        return parseInt((max - min) * me.nextRealFloat() + min);
    };

    !function(){
        for (var i = 0; i < max; i++) {
            stack.push(Math.random());
        }
    }();
};

var Rain = function(){
    var me = this;
    var rainCount = 5000;
    var canvas;
    var engine;
    var width;
    var height;
    var midWidth;
    var midHeight;
    var debug = [];
    var rain;
    var rainParts = 5;
    var rainColor = [0xff, 0x44];
    var random;
    var tickCounter   = 0;
    var gravityPoints = [];
    var gravityProcc;
    var distanceNormalizer;
    var visibility = {
        current : 0.2,
        target  : 0.2
    };

    var mainWind = {
        current : 0,
        target  : 0
    };

    var createRain = function()    {
        rain = [];
        for (var i = 0, sy; i < rainCount; i++) {
            sy = random.nextRealFloat();
            rain.push({
                x  : getRandomXRainPos(),
                y  : random.nextRealInt(0, sy * height), //abs y pos
                sx : 0, //move x per tick
                sy : sy, //move y per tick
                r  : random.nextRealFloat(), //kp
                ge : random.nextFloat(), //gravity/wind dependency
                v  : random.nextRealFloat() //visibility
            });
        }
    };

    var setup = function()    {
        random = new FastRandom();
        canvas = document.getElementById("scene");
        width  = window.innerWidth;
        height = window.innerHeight;
        midWidth  = parseInt(width / 2);
        midHeight = parseInt(height / 2);
        canvas.setAttribute("width", width);
        canvas.setAttribute("height", height);
        engine = canvas.getContext("2d");
        createRain();
        changeWindTarget();
        solveGravityProcc();
        distanceNormalizer = (width + height) / 2;
        if (!engine) {return false;}
        if (!window.requestAnimationFrame) {return false;}
        return true;
    };

    var solveX = function(percentageX)    {
        return parseInt(width / 100 * percentageX);
    };

    var solveY = function(percentageY)    {
        return parseInt(height / 100 * percentageY);
    };


    var drawDarkness = function()    {
        engine.clearRect(0, 0, width, height);
    };

    var getRandomXRainPos = function()    {
        return random.nextFloat() * width + 1000 * 1000;
    };

    var scaleToGrey = function(scale)    {
        scale = scale | 0;
        return "#" + (scale + (scale << 8) + (scale << 16)).toString(16);
    };

    var renderRain = function()    {
        engine.lineWidth   = 2;
		var absX, absBaseX;
        var moveX, moveY;
        var partSize  = parseInt(rain.length / rainParts);
        var colorStep = parseInt((rainColor[0] - rainColor[1]) / rainParts);
        for (var i = 0, baseIndex; i < rainParts; i++) {
            baseIndex = i * partSize;

            engine.beginPath();
            engine.strokeStyle = scaleToGrey(rainColor[0] - colorStep * i);

            for (var d = baseIndex, max = baseIndex + partSize, drops; d < max; d++) {

                drops = rain[d];

                absBaseX = drops.x % width;
                moveY    = (height / 300) + drops.sy * height / 100;
                moveX    = mainWind.current * drops.sy * drops.r;

                gravityPoints.forEach(function(gravPoint) {

                    var distance    = Math.sqrt(Math.pow(gravPoint.x - absBaseX, 2) + Math.pow(gravPoint.y - drops.y, 2));
                    var gravity     = Math.max(0.05, 1 - distance * 2 / distanceNormalizer) * Math.sin(Math.PI / gravPoint.tm * gravPoint.t);
                    var move        = width / 1000 * (gravity + 0.3) * drops.ge;

                    if (gravPoint.d) {
                        drops.sx += move;
                    } else {
                        drops.sx -= move;
                    }
                    moveY += (gravPoint.y - drops.y) / 10 * (gravity / 3);
                });

                drops.sx *= 0.95;

                var completeMoveX = moveX + drops.sx;
                drops.x += completeMoveX;
                drops.y += moveY;

                if (drops.y > drops.sy * height) {
                    drops.y = random.nextInt(-50, 0);
                    drops.x = getRandomXRainPos();
                }

                if (drops.v > visibility.current) {
                    continue;
                }
                absX = parseInt(drops.x % width);
                engine.moveTo(absX, drops.y | 0);
                engine.lineTo(
                    parseInt(absX + completeMoveX  * drops.sy),
                    parseInt((drops.y | 0) + 10 * drops.sy + height / 200)
                );
            }
            engine.closePath();
            engine.stroke();
        }
    };

    var changeWindTarget = function()    {
        mainWind.target = (0.5 - random.nextFloat()) * width / 30;
    };

    var solveWind = function()
    {
        if (random.nextFloat() < 0.01) { changeWindTarget(); }
        if (mainWind.current === mainWind.target) { return; }
        var diff = mainWind.target - mainWind.current;

        if (Math.abs(diff) < 0.01) {
            mainWind.current = mainWind.target;
            return;
        }
        mainWind.current += diff / 200;
    };

    var solveGravityProcc = function()    {
        gravityProcc = random.nextInt(100, 400);
    };

    var solveGravity = function()    {
        if (tickCounter % gravityProcc === 0) {
            solveGravityProcc();
            var t = random.nextInt(100, 300);
            gravityPoints.push({
                x : random.nextInt(0, width),
                y : random.nextInt(0, height),
                t : t,
                tm : t,
                d : random.nextFloat() < 0.5 ? 0 : 1
            });
        }

        var remove = [];
        for (var i = 0; i < gravityPoints.length; i++) {
            gravityPoints[i].t--;

            if (gravityPoints[i].t === 0) {
                remove.push(i);
            }
        }

        for (var r = 0; r < remove.length; r++) {
            gravityPoints.splice(remove[r] - r, 1);
        }
    };

    var changeVisibility = function()
    {
        visibility.current += (visibility.target - visibility.current) / 100;

        if (tickCounter % 240 === 0) {
            visibility.target = random.nextRealFloat();
        }
    };

    var tick = function()
    {
        drawDarkness();
        renderRain();
        solveWind();
        solveGravity();
        changeVisibility();
        register();
        tickCounter++;
    };

    var register = function()
    {
        window.requestAnimationFrame(tick);
    };

    this.run = function()
    {
        if (!setup()) {
            return;
        }
        engine.fillStyle = "rgba(0,0,0,0)";
        engine.fillRect(0, 0, 100, 100);
        register();
    }
};

var r = new Rain();
r.run();
</script>
</body>
</html>
')]
}]

