}

function createRain() {
  for (var i = 0; i < rainnum; i++) {
    rain[i] = {
      x: Math.random() * w,
      y: Math.random() * h,
      l: Math.random() * 1,
      xs: -4 + Math.random() * 4 + 2,
      ys: Math.random() * 10 + 10
    };
  }
}

function createLightning() {
  var x = random(100, w - 100);
  var y = random(0, h / 4);

  var createCount = random(1, 3);
  for (var i = 0; i < createCount; i++) {
    single = {
      x: x,
      y: y,
      xRange: random(5, 30),
      yRange: random(10, 25),
      path: [{
        x: x,
        y: y
      }],
      pathLimit: random(40, 55)
    };
    lightning.push(single);
  }
};

function drawRainTrough(i) {
  tsCtx1.beginPath();
  var grd = tsCtx1.createLinearGradient(0, RainTrough[i].y, 0, RainTrough[i].y + RainTrough[i].length);
  grd.addColorStop(0, "rgba(255,255,255,0)");
  grd.addColorStop(1, "rgba(255,255,255," + RainTrough[i].opacity + ")");

  tsCtx1.fillStyle = grd;
  tsCtx1.fillRect(RainTrough[i].x, RainTrough[i].y, 1, RainTrough[i].length);
  tsCtx1.fill();
}

function drawRain(i) {
  tsCtx2.beginPath();
  tsCtx2.moveTo(rain[i].x, rain[i].y);
  tsCtx2.lineTo(rain[i].x + rain[i].l * rain[i].xs, rain[i].y + rain[i].l * rain[i].ys);
  tsCtx2.strokeStyle = "rgba(174,194,224,0.5)";
  tsCtx2.lineWidth = 1;
  tsCtx2.lineCap = "round";
  tsCtx2.stroke();
}

function drawLightning() {
  for (var i = 0; i < lightning.length; i++) {
    var light = lightning[i];

    light.path.push({
      x: light.path[light.path.length - 1].x + (random(0, light.xRange) - (light.xRange / 2)),
      y: light.path[light.path.length - 1].y + (random(0, light.yRange))
    });

    if (light.path.length > light.pathLimit) {
      lightning.splice(i, 1);
    }

    tsCtx3.strokeStyle = "rgba(255, 255, 255, .1)";
    tsCtx3.lineWidth = 3;
    if (random(0, 15) === 0) {
      tsCtx3.lineWidth = 6;
    }
    if (random(0, 30) === 0) {
      tsCtx3.lineWidth = 8;
    }

    tsCtx3.beginPath();
    tsCtx3.moveTo(light.x, light.y);
    for (var pc = 0; pc < light.path.length; pc++) {
      tsCtx3.lineTo(light.path[pc].x, light.path[pc].y);
    }
    if (Math.floor(random(0, 30)) === 1) { //to fos apo piso
      tsCtx3.fillStyle = "rgba(255, 255, 255, " + random(1, 3) / 100 + ")";
      tsCtx3.fillRect(0, 0, w, h);
