			RainTrough[i].y = h - RainTrough[i].y - RainTrough[i].length * 5;
		} else {
			RainTrough[i].y += speedRainTrough;
		}
		drawRainTrough(i);
	}
}

function animateRain() {
	cleartsCanvas2();
	for (var i = 0; i < rainnum; i++) {
		rain[i].x += rain[i].xs;
		rain[i].y += rain[i].ys;
		if (rain[i].x > w || rain[i].y > h) {
