		createLightning();
		lightTimeCurrent = 0;
		lightTimeTotal = 200;  //rand(100, 200)
	}
	drawLightning();
}

function init() {
	createRainTrough();
	createRain();
	window.addEventListener("resize", createRainTrough);
}
init();

function animloop() {
	animateRainTrough();
	animateRain();
	animateLightning();
	requestAnimationFrame(animloop);
}
animloop();
</script>
