								_g.lineTo(this.particles[i + 1].position.x * retina, this.particles[i + 1].position.y * retina);
								_g.lineTo(((this.particles[i + 1].position.x + p1.x) * 0.5) * retina, ((this.particles[i + 1].position.y + p1.y) * 0.5) * retina);
								_g.closePath();
								_g.stroke();
