					for (i = 0; i < confettiRibbonCount; i++) {
						confettiRibbons[i] = new ConfettiRibbon(random() * canvasWidth, -random() * canvasHeight * 2, ribbonPaperCount, ribbonPaperDist, ribbonPaperThick, 45, 1, 0.05);
					}
					var confettiPapers = new Array();
