macros.command:
* {
	box-sizing: border-box;
}

body {
	font-size: 16px;
	padding: 0.5rem;
	color:white;
}

div {
	padding: 0;
	margin: 0;
}

.hidden {
	display: none;
}

:not(.multiple-selected) .hide-for-single {
	display: none;
}

.multiple-selected .hide-for-multiple {
	display: none;	
}

.selected-token {
	display:flex;
	align-items: middle;	
	color:white;
}

.selected-token__image {
	margin-right: 0.75rem;
	margin-top: 0.75rem;
}

.selected-token__content {
	width: 200px;
}

.multiple-selected .selected-token__content h2 {

}

h1.selected-header,
.selected-token__content h1 {
	font-family: 'Times', 'Helvetica', serif;
	font-size: 1rem;
	text-transform: uppercase;
	margin: 0;
	text-shadow: 1px 1px 0 black, -1px 1px 0 black, 1px -1px 0 black, -1px -1px 0 black;	
}

.selected-token__content h2 {
	font-family: 'Times', 'Helvetica', serif;
	font-size: 3rem;
	margin: 0;
	color: white;
	white-space: nowrap;
	text-shadow: 2px 2px 0 black, -2px 2px 0 black, 2px -2px 0 black, -2px -2px 0 black;
}

.multiple-selected .selected-token__content h2 {
	font-size: 1.5rem;
}

.selected-token__content__hp {
	position:relative;
	background-color: rgba( 0, 0, 0, 0.5);
	border: 2px solid black
}

.selected-token__content__hp__bar {
	height:1.5rem;
	background-color: green;
}

.selected-token__content__hp__value {
	position: absolute; 
	top: 0;
	padding: 0.25rem 0 0 0.25rem;
	color: white;	
}

/* All divs are assumed to be overlays. */
.overlay {
  position: fixed;
  box-sizing: border-box;
  margin: 0;
}

.overlay__flex-content {
  display: flex;
  flex-direction: column;
  align-items: stretch;
}

.overlay__child {
  display: block;  
}

.top {
  top: 0;
}

.bottom {
  bottom: 0;
}

.left {
  left: 0;
}

.right {
  right: 0;
}

.center {
  left: 50%;
  transform: translateX(-50%);
}

.middle {
  top: 50%;
  transform: translateY(-50%);
}

.middle.center {
  transform: translate(-50%, -50%);
}

/* All overlays would be assigned an ID. 
 Here is an example of an overlay offset applied with a wrapper div and explicit offset class. */
#OVERLAY-001 .overlay__content.offset {
  position: relative;
  top: 16px;
  left: 16px;
}

/* all styles for an overlay would be prefixed with an ID and a wrapper element to scope targeting */
#OVERLAY-002.options {  
  max-width: 75%;
  margin: 0 auto;
}

#OVERLAY-002 .overlay__content img {
  display: block;
  width: 100%;
}

#OVERLAY-003.options {
  color: white;
  margin-right: 1rem;
}

#OVERLAY-003 .overlay__child:not(.overlay__content) {
  color: lightblue;
  padding: 0.5rem;
  margin-bottom: 0.25rem;
  font-size: 16px;
  border: 1px solid rgba(255, 255, 255, 0.5);
}

/* Example of a responsive overlay rule */
@media screen and (max-width: 600px) {
  #OVERLAY-002.options {
    width: 100%;
    max-width: unset;
  }
}

/* Utility styles for this demonstration */
body {
  background-size: cover;
  color: white;
  font-size: 20px;
  font-weight: bold;
  font-family: Helvetica, Calibri, Roboto, Arial, sans-serif;
}

.example {
  height: 100px;
  width: 150px;
  background: rgba(128, 128, 255, 0.25);
  border: 10px solid rgba(255, 128, 128, 0.5);
  padding: 1rem;
  text-align: center;
}

.hidden {
  display: none;
}

[h, if(!isOverlayRegistered(macro.args)), code: {
	[macro(macro.args + "@" + getMacroLocation()): ""]
};{
	[closeOverlay(macro.args)]
}]

[r: vSelected = getSelected()]
[h, if(listCount(vSelected) == 1), code: {
	[vIsSmartDoor = getProperty("door_smart", vSelected)]
	[vIsSmartDoor = if(vIsSmartDoor == "", 0, vIsSmartDoor)]
	[if(vIsSmartDoor), code: {	
		[macro("toggle.open@this"): ""]
	};{}]
};{}]

[overlay(getMacroName()): {
<html>
<head>
	<link rel="onChangeSelection" type="macro" href="[r: macroLinkText(getMacroName() + "@" + getMacroLocation())]">
</head>
<body style="margin: 0; padding: 0; font-size:12px; color: white;">
	<div style="position:fixed; margin:0; padding: 0.25rem; bottom:0; left: 0; background-color:rgba(0,0,0,.5);">
		DOOR WATCHER ON
	</div>
</body>
</html>
}]

[h: vSelected = getSelected()]
[h: switchToken(vSelected)]
[h, if(indexOf(macro.args, 'override')>-1): override=1; override=0]
[h, if(indexOf(macro.args, 'peek')>-1): peeking=1; peeking=0]

[h: door.i18n=getProperty('door_i18n')][h: door.language=getProperty('door_language')]
[h: i18n_1=json.path.read(door.i18n, strformat('["%{door.language}"]["message_05"]'))]
[h: i18n_2=json.path.read(door.i18n, strformat('["%{door.language}"]["message_06"]'))]

[h: isOpen=getProperty('door_open')]
[h: isOpen=if(isOpen=='', if(json.equals(getTokenVBL(), getProperty('door_open_vbl')), 1, 0), isOpen)]
[h: isSwing=if(number(getProperty('door_type'))==0, 1, 0)]
[h, if(isSwing==''), code:{
	[isSwing=!json.isEmpty(getMacroIndexes('Swing Door', 'json'))]
	[setProperty('door_type', isSwing)]};{}]
[h: isLocked=getProperty('door_locked')]
[h, if(isLocked==''), code:{
	[isLocked=!json.isEmpty(getMacroIndexes('Locked', 'json'));'']
	[setProperty('door_locked', isLocked)]};{}]
[h: isDouble=if(getProperty('door_paired_id')!='', 1, 0)]
[h, if(getTokenFacing()==''): setTokenFacing(-90); '']

[h, if(isLocked), code:{
	[if(!override && !peeking), code:{
		[broadcast(i18n_1)]
		[abort(0)]
	};{}]
};{}]
[h: isPeek=number(getProperty('door_peek'))]
[h, if(peeking), code:{
	[isPeekingAllowed=number(getProperty('door_peek_allow'))]
	[if(!isPeekingAllowed && !isOpen), code:{
		[broadcast(i18n_2)]
		[abort(0)]
	};{}]
	[if(isPeekingAllowed && !isOpen), code:{
		[setTokenVBL(if(isPeek, getProperty('door_closed_VBL'), getProperty('door_peek_VBL')))]
		[setProperty('door_peek', !isPeek)]
		[abort(0)]
	};{}]
	[abort(0)]
};{
	[if(isPeek): setTokenVBL(if(isOpen, getProperty('door_open_VBL'), getProperty('door_closed_VBL')))]
}]

[h, if(isSwing), code:{
	[doorSwingArc=if(isNumber(getLabel()), getLabel(), getProperty('door_arc'))]
	[startFacing=getTokenFacing()]
	[direction=if(isOpen, -1, 1)]
	[facingChange=direction*doorSwingArc]
	[setTokenFacing(startFacing+facingChange)]
};{
	[if(isOpen), code:{
		[setTokenImage(getTokenHandout())]
		[setTokenVBL(getProperty('door_closed_VBL'))]
	};{
		[setTokenImage(getTokenPortrait())]
		[setTokenVBL(getProperty('door_open_VBL'))]
	}]
}]
[h: setProperty("door_open", !isOpen)]

[h: '<!-- action second door if paired -->']
[h: pairedId=getProperty('door_paired_id')]
[h, if(pairedId==""), code:{
	[exposePCOnlyArea()]
	[abort(0)]
};{
	[switchToken(pairedId)]
}]

[h: isSwing=if(number(getProperty('door_type'))==0, 1, 0)]
[h, if(isSwing), code:{
	[doorSwingArc=if(isNumber(getLabel()), getLabel(), getProperty('door_arc'))]
	[startFacing=getTokenFacing()]
	[direction=if(isOpen, -1, 1)]
	[facingChange=direction*doorSwingArc]
	[setTokenFacing(startFacing+facingChange)]
};{
	[if(isOpen), code:{
		[setTokenImage(getTokenHandout())]
		[setTokenVBL(getProperty('door_closed_VBL'))]
	};{
		[setTokenImage(getTokenPortrait())]
		[setTokenVBL(getProperty('door_open_VBL'))]
	}]
}]
[h: setProperty("door_open", !isOpen)]
[h: exposePCOnlyArea()]

