[h: macro.return = 
listFind(
	"a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z", 
	lower(
		substring(
			arg(0), 0, min(length(arg(0)), 1)
		)
	)
) + 65]

[h: "<!-- Guarantee OCL has run. -->"]
[h: vOCL = isFunctionDefined("OCL." + getLibProperty("libOCL")]
[h, if(!vOCL): execLink(macroLinkText("onCampaignLoad@" + getMacroLocation()))]

[h: "<!-- Grab a list of Library Tokens. -->"]
[h: vLibInfo = librat.getLibs()]
[h: vLibs = json.sort(json.fields(vLibInfo, "json"))]

[h: "<!-- Set style variables, define CSS, and render frame. -->"]
[h: vFrameTitle = "MapTool Library Revision Admin Tools"]
[h: vFrameProps = ""]
[h: vLibMenuItemHeight = "6rem"]
[h: vCSS = "
:root {
	font-size: 12px;
}

body {
	font-size: inherit;
}

::-webkit-scrollbar {
  -webkit-appearance: auto;
  background-color: rgba(255, 128, 64, .1);
  width: .75rem;
}

::-webkit-scrollbar-thumb {
  background-color: rgba(48, 48, 32, .25);
}

a {
	text-decoration: none;
}

div {
	margin: 0;
	padding: 0;
}

.lib-menu {
	padding: 0;
	list-style: none;
}

.lib-menu__item {
	display: flex;
	flex-direction: row;
	min-height: " + vLibMenuItemHeight + ";
	margin-bottom: .5rem;
	background: white;
}

.lib-menu__item__img { 
	display: block;
	height: " + vLibMenuItemHeight + ";
}

.lib-menu__item__info {
	flex-grow: 2;
	padding: .5rem;
	padding-bottom: 0;
}

.lib-menu__item__info__libname {
	font-weight: bold;
	font-size: 1.25rem;	
}

.lib-menu__item__updates {
	text-align: right;
	padding: .5rem;
	padding-bottom: 0;
	color:gray;
}

.lib-menu__item__updates--outdated {
	color:red;
}

.lib-menu__item__updates--current {
	color:green;
}

"]

[frame5(vFrameTitle, vFrameProps): {
<html>
<head>
<style>[r: vCSS]</style>
</head>
<body>
<ul class="lib-menu">
[h: "<!-- Loop to render Library token data. -->"]
[r, foreach(vLib, vLibs, ""), code: {

<li class="lib-menu__item">
<img class="lib-menu__item__img" src="[r: getImage(vLib)]">
<div class="lib-menu__item__info">	
	<div class="lib-menu__item__info__libname">[r: vLib]</div>
	[r, if(getLibProperty("libTitle", vLib) != ""): '
	<div class="lib-menu__item__info__libtitle">' + getLibProperty("libTitle", vLib) + '</div>']
	<div class="lib-menu__item__info__author">
		[r, if(getLibProperty("libAuthorURI", vLib) != ""): 
			"<a href='" + getLibProperty("libAuthorURI", vLib) + "'>"]
		[r, if(getLibProperty("libAuthor", vLib) != ""): getLibProperty("libAuthor", vLib); "Unknown Author"]
		[r, if(getLibProperty("libAuthorURI", vLib) != ""): "</a>"]
	</div>
	<div class="lib-menu__item__info__version">
		[r, if(getLibProperty("libVersion", vLib) != ""): getLibProperty("libVersion", vLib); "Unknown Version"]
	</div>
</div>
[h: vUpdateStatus = table("status")]
<div class="lib-menu__item__updates lib-menu__item__updates--[r: vUpdateStatus]">
[r: capitalize(vUpdateStatus)]
</div>
</li>
}]
</ul>
</body>
</html>
}]

[r: macro.return = '
{
	"lib:OnTokenMove" : { 
		"48b" : {
			"mtversion" :  "1.8.4",
			"notes" :      {},
			"gmnotes" :    {},
			"properties" : {},
			"macros" :     {}
		}, 
		"48a" : {
			"mtversion" :  "1.8.3"
		},
		"48" : {
			"mtversion" :  "1.8.3"
		} 
	}
	"Lib:Rat" : {
		"0.1a" : {
			"mtversion" :  "1.8.4",
			"notes" :      {},
			"gmnotes" :    {},
			"properties" : {},
			"macros" :     {}
    	}
	}
}']

[h: "<!-- If the argument count is incorrect, take user input. -->"]
[h, if(argCount() == 1), code: {
	[vAlphaString     = lower(arg(0))]
	[vClicked         = false]
};{
	[abort(input("vAlphaString | rc | Left Semver alpha group | TEXT |"))]
	[vAlphaString = lower(vAlphaString)]
 	[vClicked = true]
}]

[h: "<!-- No alpha string is a release version. -->"]
[h, if(vAlphaString == 0 || vAlphaString == ""), code: {
	[vReturn = 2000)]
	[if(vClicked): broadcast("librat.atoi.semver: " + vReturn)]
	[return(0, vReturn)]
}]

[h: "<!-- Elevate 'rc' above othre pre-release versions. -->"]
[h, if(vAlphaString == "rc"), code: { 
	[vReturn = 1900)]
	[if(vClicked): broadcast("librat.atoi.semver: " + vReturn)]
	[return(0, vReturn)]
};{
	[vReturn = librat.atoi(vAlphaString)]
	[if(vClicked): broadcast("librat.atoi.semver: " + vReturn))]	
	[return(0, vReturn)]
}]

