[r: strformat('
<html>
<head>
<title>Bubbles</title>
<meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1">
<style>%{css}
</style>
</head>
<body>
<div class="container" id="container"></div>
<script>
"use strict"  
var fov                       = %{fov}                      // changes perspective angle
var bubblesize                = %{bubblesize}               // big or small bubbles multiplier
var bubblefactor              = %{bubblefactor}             // number of bubbles multiplier
var wobbleStrength            = %{wobbleStrength}           // how far do bubbles wobble multiplier
var wobbleOmegaFactor         = %{wobbleOmegaFactor}        // rotational velocity of wobble multiplier
var bubbleAcceleration        = %{bubbleAcceleration}       // determines speed of rise
var bubbleDepth               = %{bubbleDepth}              // distance travelled
var bubbleMassBase            = %{bubbleMassBase}           // bubble mass determines degree of effect of current flow
var bubbleMassVariation       = %{bubbleMassVariation}      
var currentOmegaFactor        = %{currentOmegaFactor}       // rotational velocity of changing current
var backgroundGradientOpacity = %{backgroundGradientOpacity}
var bubbleCanvasOpacity       = %{bubbleCanvasOpacity}      

var cnv0;
var ctx0;

var wWidth = window.innerWidth;
var wHeight= window.innerHeight;
var eye = new Vector3D(wWidth/2, wHeight/2, Math.max(wWidth, wHeight));

var bubbles = new Array();
var bubbleRadius = 14 * bubblesize;
var maxbubbles = Math.floor(100 * bubblefactor);

var zStart = 1;
var zEnd = bubbleDepth;
var riserate = new Vector3D(0,0,bubbleAcceleration);

var t0, dt, animId;
var fps = 60;

var force;
var acc;
var forceField;
var forceTheta = 0;

function Forces(){}
Forces.zeroForce = function() {	return (new Vector3D(0,0,0));}
Forces.forceField = function(q,E) {	return E.multiply(q);}
Forces.add = function(arr){
		var forceSum = new Vector3D(0,0,0);
		for (var i=0; i<arr.length; i++){
		var force = arr[i];
		forceSum.incrementBy(force);
	}
	return forceSum;
}

function Vector3D(x,y,z) {
	this.x = x;
	this.y = y;	
	this.z = z;	
}		
Vector3D.prototype = {		
	add: function(vec) {
		return new Vector3D(this.x + vec.x,this.y + vec.y,this.z + vec.z);
	},
	incrementBy: function(vec) {
		this.x += vec.x;
		this.y += vec.y;
		this.z += vec.z;		
	},	
	multiply: function(k) {
		return new Vector3D(k*this.x, k*this.y, k*this.z);
	},	
	addScaled: function(vec,k) {
		return new Vector3D(this.x + k*vec.x, this.y + k*vec.y, this.z + k*vec.z);
	}
};
function Particle(radius,mass){
	if(typeof(radius)==="undefined") radius = 5;
	if(typeof(mass)==="undefined") mass = 1;	
	this.radius = bubbleRadius;
	this.mass = mass;
	this.x = 0;
	this.y = 0;
	this.z = 1;
	this.vx = 0;
	this.vy = 0;
	this.vz = 0;
	this.opacity = Math.random() * 100 / 1000;
	this.wobble = [Math.random() * Math.PI * 2, (Math.random() - 0.5) / this.z]
}
Particle.prototype = {
	get pos3D (){return new Vector3D(this.x,this.y,this.z);},
	set pos3D (pos){
		this.x = pos.x;
		this.y = pos.y;
		this.z = pos.z;
	},
	get velo3D (){return new Vector3D(this.vx,this.vy,this.vz);	},
	set velo3D (velo){
		this.vx = velo.x;
		this.vy = velo.y;
		this.vz = velo.z;
	},
	recycle: function() {
		this.pos3D = new Vector3D(Math.random()*wWidth-wWidth/2, Math.random()*wHeight-wHeight/2, zStart);
		this.mass = Math.random()*bubbleMassVariation+bubbleMassBase;
		this.velo3D = new Vector3D(0,0,riserate.z).multiply(Math.random()*2+2);
		this.radius = Math.random() * Math.random() * bubbleRadius;
		this.opacity = Math.random() * 100 / 1000;
		this.wobble = [
			Math.random() * wobbleOmegaFactor * Math.PI * 2, 
			(Math.random() - 0.5) * wobbleStrength / this.z
			]
	},
	draw: function (ctx) { 
		if(this.z < -fov) {return};
		var scale = (fov+this.z)/fov;
		var r    = this.radius * scale;
		var x2d  = (this.x * scale) + wWidth/2;
		var y2d  = (this.y * scale) + wHeight/2;
		var x2dw = x2d + Math.min(Math.cos(this.wobble[0]) * this.wobble[1], r/2-4);
		var y2dw = y2d + Math.min(Math.sin(this.wobble[0]) * this.wobble[1], r/2-4);


		var gradient = ctx.createRadialGradient(x2dw, y2dw, 0, x2d, y2d, r);
		gradient.addColorStop("0"   , "transparent");
		gradient.addColorStop("0.5" , "rgba(255,255,255," + this.opacity / 2 + ")");
		gradient.addColorStop("0.75", "rgba(255,255,255," + this.opacity + ")");
		gradient.addColorStop("0.95" , "rgba(255,255,255," + this.opacity * 1.2 + ")");
		gradient.addColorStop("1.0"   , "transparent");

		ctx.save();
		ctx.beginPath();
		ctx.ellipse(x2d, y2d, r, r + Math.cos(this.wobble[0]/10),
			this.wobble[0], 0, Math.PI*2, true);
		ctx.rotate(this.wobble[0]/40)
		ctx.scale(1+Math.cos(this.wobble[0])/12, 1+Math.sin(this.wobble[0])/13);

		ctx.translate(
			Math.cos(this.wobble[0]) * this.wobble[1], 
			Math.sin(this.wobble[0]) * this.wobble[1]
			);


		ctx.fillStyle = gradient;
		ctx.arc(x2d, y2d, r, 0, Math.PI*2, false);
		ctx.fill();

		ctx.strokeStyle = "rgba(255,255,255," + this.opacity * 2  + ")";

		ctx.stroke();
		ctx.restore();

		this.wobble[0] += Math.sign(Math.random()-0.6) * Math.random() * Math.PI * wobbleOmegaFactor / 36;
		this.wobble[1] += (Math.random() - 0.55) * 0.9 * wobbleStrength;


    if( x2d+r <0 || x2d-r > wWidth || 
		y2d+r <0 || y2d-r > wHeight ||
		this.opacity<0.01) this.recycle()

	}
};

Particle.prototype.constructor = Particle;

function init() {
	//create canvas
	cnv0 = createCanvas(cnv0);
	ctx0 = cnv0.getContext("2d");
	document.getElementById("container").style.opacity = backgroundGradientOpacity;
	cnv0.style.opacity = bubbleCanvasOpacity;
	// create bubbles
	for (var i=0; i<maxbubbles; i++){	
		var bubble    = new Particle(18,255,255,255,0.2,true);
		bubble.recycle();
		bubble.z = Math.random()*zEnd;
		bubble.draw(ctx0);
		bubbles.push(bubble);
	}	
	t0 = new Date().getTime(); 
	animFrame();
}
function createCanvas(canvas) {
	canvas = document.createElement("canvas");
	canvas.width = wWidth;
	canvas.height = wHeight;
	document.body.appendChild(canvas);
	canvas.classList.add("overheadCanvas");
	return canvas;
}

function animFrame(){
     setTimeout(function() {
          animId = requestAnimationFrame(animFrame,cnv0);
          onTimer();
     }, 1000/fps);
}

function onTimer(){
	var t1 = new Date().getTime(); 
	dt = 0.001*(t1-t0); 
	t0 = t1;
	if (dt>0.2 || dt == 0) {dt=0.001;};	
	forceTheta += Math.random()*currentOmegaFactor/(dt);
	move();
}

function move(){
	if(cnv0.width != window.innerWidth) {
		cnv0.width = wWidth = window.innerWidth;
		cnv0.height = wHeight = window.innerHeight;
		}
	ctx0.clearRect(0, 0, wWidth, wHeight);
	for (var i=0; i<bubbles.length; i++){
		var bubble = bubbles[i];
		moveObject(bubble, ctx0);
		calcForce(bubble);
		updateAccel(bubble.mass);
		updateVelo(bubble);
	};
}

function moveObject(obj, ctx){	
		obj.velo3D = obj.velo3D.addScaled(riserate,dt);
		obj.pos3D = obj.pos3D.addScaled(obj.velo3D,dt);
		if (obj.z > zEnd) obj.opacity *= 0.9 * (1-dt);
		obj.draw(ctx);
}
function updateAccel(mass){	acc = force.multiply(1/mass); }	
function updateVelo(obj)  { obj.velo3D = obj.velo3D.addScaled(acc,dt); }
function calcForce(obj)   {	
	force = Forces.zeroForce();
	forceField = Forces.forceField(
		Math.random()*0.04, 
		new Vector3D(
			Math.cos(forceTheta)*wWidth*2, 
			Math.sin(forceTheta)*wHeight/2
			,0)); 
	force = Forces.add([force, forceField]);
}
window.onload = init;
</script>
</body>
