				valueTextElement.innerText = this.value;
			});
		};
	};
	var selectInputs = document.getElementsByClassName("layermenu");
	for(var i=0; i<selectInputs.length; i++) {
		selectInputs[i].addEventListener("change", function(e) {prefClick(e, this.id);});
	}
	selectInputs = document.getElementsByClassName("soundmenu");
	for(var i=0; i<selectInputs.length; i++) {
		selectInputs[i].addEventListener("change", function(e) {sndprefClick(e, this.id);});
	}
	var accordionButtons = document.getElementsByClassName("accordionbutton");
	for(var i=0; i<accordionButtons.length; i++) {
		accordionButtons[i].addEventListener("click", function() {accordion();});
	}
});
function sndprefClick(evt, sndval) {
	var btns=document.getElementsByClassName("prefButton");
	var usebtnid=sndval+"prefbtn";
	var soundprefs = document.getElementsByClassName("soundprefs");
	var soundprefsid=usebtnid.replace("prefbtn","_prefs");

	var prefTbls = document.getElementsByClassName("prefsTable");
	for(i=prefTbls.length-1; i>-1; i--) {prefTbls[i].classList.add("hiding");} 

	for(i=0; i<btns.length; i++) { 
		if(btns[i].id != usebtnid) { btns[i].classList.remove("prefbtnactive");} else {btns[i].classList.add("prefbtnactive");}
	}

	for(i=0; i<soundprefs.length; i++) {
		if(soundprefs[i].id != soundprefsid) {soundprefs[i].classList.add("hiding");} else { soundprefs[i].classList.remove("hiding"); }
	}

}
function prefClick(evt, layerval) {
	var selVal = document.getElementById(layerval).value;	
	var idPrefsTbl=layerval+"_prefs";
	var idPrefSet=idPrefsTbl+"_"+selVal.toLowerCase().split(" ").join("");
	var idPrefOpacity =layerval+"_prefs_opacity";
	var prefTbls = document.getElementsByClassName("prefsTable");
	var btns=document.getElementsByClassName("prefButton");
	var usebtnid=layerval.replace("layer", "prefbtn");
	for(i=0; i<btns.length; i++) { 
		if(btns[i].id != usebtnid) { btns[i].classList.remove("prefbtnactive");} else {btns[i].classList.add("prefbtnactive");}
	}

	var soundprefs = document.getElementsByClassName("soundprefs");
	for(i=0; i<soundprefs.length; i++) {soundprefs[i].classList.add("hiding");}

	for(i=prefTbls.length-1; i>-1; i--) {
		if(prefTbls[i].id != idPrefsTbl) {prefTbls[i].classList.add("hiding");} else { prefTbls[i].classList.remove("hiding"); }
	}
	prefSets=document.getElementById(idPrefsTbl).getElementsByClassName("prefset");
	for(i=prefSets.length-1; i>-1; i--) {
		if(prefSets[i].id == idPrefSet || prefSets[i].id == idPrefOpacity)
			{ prefSets[i].classList.remove("hiding");} else {prefSets[i].classList.add("hiding");}
	}
}
function accordion() {
	var btns=document.getElementsByClassName("accordionbutton");
	for(var i=0; i<btns.length; i++) {
		if(btns[i].classList.contains("btnopen")) {
			btns[i].innerHTML="";
			btns[i].classList.remove("btnopen");
		} else {
			btns[i].innerHTML="";
			btns[i].classList.add("btnopen");
		}
	}
	var rows=document.getElementsByClassName("accordionrow");
	for(var i=0; i<rows.length; i++) {
		if(rows[i].classList.contains("hiding")) {
			rows[i].classList.remove("hiding")
		} else {
			rows[i].classList.add("hiding")
		}
	}	
};
</script>']
