<body>


<canvas id="emberCanvas" width="800" height="600"></canvas>
<script>
var canvas;
var canvasW, canvasH;
var context;
var fps = 26;
var t0,dt;
var k = 50;
var vortices;
var attractors;
var embers;
var force;
var forceTheta = 0;
var forceField;
var acc;
var numEmbers = 350;
var maxEmberSize;
var minEmberSize;
var numAttractors = 2;
var numVortices = 3;
var vortexLifetime=45;
var graph;
var vMax=80;

function Vortex(radius, spin)
    { this.respawn(radius, spin) }
    Vortex.prototype = {
		get pos2D (){
		return new Vector2D(this.x,this.y);
		},
		set pos2D (pos){
		this.x = pos.x;
		this.y = pos.y;
		},
		get velo2D (){
		return new Vector2D(this.vx,this.vy);
		},
		set velo2D (velo){
		this.vx = velo.x;
		this.vy = velo.y;
		}
	}
	Vortex.prototype.respawn=function(radius, spin) {
      if(typeof(radius)==="undefined") radius = canvasW/(Math.random()*7+7);
	  if(typeof(spin)==="undefined") spin = Math.sign(Math.random()-0.5)*(Math.random()*8+2);
	  this.x=canvas.width*Math.random()
      this.y=canvas.height*Math.random()
      this.vx=(Math.random()-0.5)*10
      this.vy=(Math.random()-0.5)*10
      this.life=vortexLifetime
      this.radius = radius
      this.speed=spin	
	  this.scale=canvasH*0.0016
      this.scale*=this.scale
      if (Math.random()<0.5) this.speed=-this.speed
    }

    Vortex.prototype.update=function() {
      var lifeFactor=this.life/vortexLifetime
	  var damping=1-0.005
      this.vx*=damping
      this.vy*=damping
      this.x+=this.vx
      this.y+=this.vy
      this.life-=1
      if (this.life<=0 || this.x <= 0 || this.x >= canvasW || this.y <= 0 || this.y >= canvasH) this.respawn()
    }

    Vortex.prototype.draw=function() {
		context.fillStyle = "#ffffff66";
		context.beginPath();
		context.arc(this.x, this.y, this.radius, 1*Math.PI, 0, true);
		context.closePath();
		context.fill();		
    }

function Particle(mass,charge){
	if(typeof(mass)==="undefined") mass = 1;
	if(typeof(charge)==="undefined") charge = 0;
	this.mass = mass;
	this.charge = charge;
	this.life = 0;
	this.x = 0;
	this.y = 0;
	this.vx = 0;
	this.vy = 0;
}
	Particle.prototype = {
		get pos2D (){
		return new Vector2D(this.x,this.y);
	},
	set pos2D (pos){
		this.x = pos.x;
		this.y = pos.y;
	},
	get velo2D (){
		return new Vector2D(this.vx,this.vy);
	},
	set velo2D (velo){
		this.vx = velo.x;
		this.vy = velo.y;
	}
};

function Ball(radius,color,mass,charge,gradient){
	if(typeof(radius)==="undefined") radius = 20;
	if(typeof(color)==="undefined") color = "#0000ff";
	if(typeof(mass)==="undefined") mass = 1;
	if(typeof(charge)==="undefined") charge = 0;
	if(typeof(gradient)==="undefined") gradient = false;	
	this.radius = radius;
	this.color = color;
	this.mass = mass;
	this.charge = charge;
	this.gradient = gradient;
	this.x = 0;
	this.y = 0;
	this.vx = (Math.random()-0.5)*8;
	this.vy = (Math.random()-0.5)*5;
}		
Ball.prototype = Object.create(Particle.prototype);
Ball.prototype.constructor = Ball;
Ball.prototype.draw = function (context){
	if (this.gradient){
		grad = context.createRadialGradient(this.x,this.y,0,this.x,this.y,this.radius);
		grad.addColorStop(0,"#ffff00");
		grad.addColorStop(1,this.color);
		context.fillStyle = grad;
	}else{
		context.fillStyle = this.color;
	}	
	context.beginPath();
	context.arc(this.x, this.y, this.radius, 0, 2*Math.PI, true);
	context.closePath();
	context.fill();		
};


function Vector2D(x,y) {
	this.x = x;
	this.y = y;		
}
Vector2D.prototype = {		
	lengthSquared: function(){
		return this.x*this.x + this.y*this.y;
	},
	add: function(vec) {
		return new Vector2D(this.x + vec.x,this.y + vec.y);
	},
	incrementBy: function(vec) {
		this.x += vec.x;
		this.y += vec.y;
	},		
	subtract: function(vec) {
		return new Vector2D(this.x - vec.x,this.y - vec.y);
	},	
	multiply: function(k) {
		return new Vector2D(k*this.x,k*this.y);
	},		
	addScaled: function(vec,k) {
		return new Vector2D(this.x + k*vec.x, this.y + k*vec.y);
	}
};

function Forces(){}
Forces.zeroForce = function() {
	return (new Vector2D(0,0));
}
Forces.electric2 = function(k,q1,q2,r){
	return r.multiply(k*q1*q2/(r.lengthSquared()));
}
Forces.forceField = function(q,E) {
	return E.multiply(q);
}
Forces.vortex = function(obj, vortex) {
	var dx=obj.x-vortex.x
	var dy=obj.y-vortex.y
	var speed=vortex.speed
	var vx=-dy*speed+vortex.vx
	var vy= dx*speed+vortex.vy
	var factor=1/(1+(dx*dx+dy*dy)/vortex.scale)
	var lifeFactor=vortex.life/vortexLifetime
	factor*=(1-lifeFactor)*lifeFactor*4
	obj.vx+=(vx-obj.vx)*factor
	obj.vy+=(vy-obj.vy)*factor
	return (new Vector2D(obj.vx, obj.vy));
}
Forces.add = function(arr){
		var forceSum = new Vector2D(0,0);
		for (var i=0; i<arr.length; i++){
		var force = arr[i];
		forceSum.incrementBy(force);
	}
	return forceSum;
}

function init() {
	canvas = document.getElementById("emberCanvas");
	canvas.width  = window.innerWidth;
	canvas.height = window.innerHeight;
	canvasW = canvas.width;
	canvasH = canvas.height;
	context = canvas.getContext("2d"); 
	vortices = new Array();
	for (var i=0; i<numVortices; i++){	
		var vortex = new Vortex(canvasW/(Math.random()*7+7), 8);	
		vortices.push(vortex);
	}	
	attractors = new Array();
	for (var i=0; i<numAttractors; i++){	
		var attractor = new Ball(canvasW/(Math.random()*5+1),"#88000000",10000,10,false);	
		attractor.pos2D = new Vector2D(canvasW,Math.random()*canvas.height);
		attractor.velo2D = new Vector2D((Math.random())*-50-100	,(Math.random()-0.5)*50);
		attractor.charge = -5;
		attractors.push(attractor);
	}
	numEmbers = Math.floor(canvasW*canvasH/750);
	minEmberSize = Math.min(canvasW/canvasH, canvasH/canvasW);
	maxEmberSize = Math.max(canvasW/canvasH, canvasH/canvasW)+1.78;
	embers = new Array();
	for (var i=0; i<numEmbers; i++){ createNewEmbers(embers);}
	t0 = new Date().getTime(); 
	animFrame();
}

function createNewEmbers(embers) {
	var size = Math.max(minEmberSize, Math.random()*maxEmberSize);
	var ember = new Ball(size,"#88000033",1, 1, true);
    ember.pos2D = new Vector2D(Math.random()*canvasW,Math.random()*canvasH);
    ember.life = Math.floor(Math.random()*300);
	ember.draw(context);
	embers.push(ember);
}
function recycleEmber(obj){
	obj.pos2D  = new Vector2D(Math.random()*canvasW,Math.random()*canvasH);
	obj.velo2D = new Vector2D(0,0);
	obj.radius = Math.max(minEmberSize, Math.random()*maxEmberSize);
	obj.life = Math.floor(Math.random()*300);
}
function animFrame(){
     setTimeout(function() {
          animId = requestAnimationFrame(animFrame,canvas);
          onTimer();
     }, 1000/fps);
}

function onTimer(){
	var t1 = new Date().getTime(); 
	dt = 0.001*(t1-t0); 
	t0 = t1;
	if (dt>0.2) {dt=0;};
	forceTheta += Math.random()/10;
	move();
}
function move(){
	context.clearRect(0, 0, canvasW, canvasH);
	forceField = Forces.forceField(Math.random()/10, new Vector2D(Math.cos(forceTheta)*canvasW*2, Math.sin(forceTheta)*canvasH/2)); 
	for (var i=0; i<vortices.length; i++){
		var vortex=vortices[i];
		calcForce(vortex);
		vortex.update();
