		var ember = embers[i];
		ember.life -= 1;
		if(ember.life == -1) {recycleEmber(ember);}
		moveEmber(ember);
		calcForce(ember);
		updateAccel(ember.mass);
		updateVelo(ember);				
	}
	for (var i=0; i<attractors.length; i++){
		var attractor = attractors[i];	
		moveAttractor(attractor);
	}
}
function moveEmber(obj){
	obj.pos2D = obj.pos2D.addScaled(obj.velo2D,dt);
	if(obj.vx < -vMax || obj.vx > vMax) { obj.vx = obj.vx * 0.63 }
	if(obj.vy < -vMax || obj.vy > vMax) { obj.vy = obj.vy * 0.63 }
	if (obj.x < 0 	    ){obj.x = canvasW; obj.vx = obj.vx*0.1}
	if (obj.x > canvasW ){obj.x = 0; 	   obj.vx = obj.vx*0.1}
	if (obj.y < 0	    ){obj.y = canvasH; obj.vy = obj.vy*0.1}
	if (obj.y > canvasH ){obj.y = 0;	   obj.vy = obj.vy*0.1}
	obj.draw(context);
}
function moveAttractor(obj){
	obj.pos2D = obj.pos2D.addScaled(obj.velo2D,dt);	
	if (obj.x <= 0 || obj.x >= canvasW || obj.y <= 0 || obj.y >= canvasH){
		recycleAttractor(obj);
		}
	obj.draw(context);	
}
function recycleAttractor(obj){
	obj.pos2D = new Vector2D(canvasW,Math.random()*canvasH);
	obj.velo2D = new Vector2D((Math.random())*-50-100,(Math.random()*Math.random()-0.5)*40);
	obj.radius = canvasW/(Math.random()*5+1);
}
