		var swingArc = Math.random()*Math.random();
	}
	var particle;

	for(var i=0; i < num; i++)	{
		particle = particles[i];
		if(i == 0) {
			particle.life      = Math.floor(Math.random()+Math.random()/0.8*prt.life);
			particle.pos2D     = startPoint(Math.random()*winLength);
			particle.lastpos   = particle.pos2D;
			particle.velo2D    = startDirection(particle.pos2D);
			particle.velo2D    = particle.velo2D.multiply(bulletSpeed);
			particle.colours   = prt.colours[Math.floor(Math.random()*prt.colours.length)];
			particle.startframe= frame + Math.floor(Math.random()*maxParticles*timescale);
		} else {
			particle.life      = particles[0].life;
			particle.pos2D     = particles[0].pos2D;
			particle.lastpos   = particles[0].pos2D;
			particle.velo2D    = particles[0].velo2D.rotate(-swingArc/2+swingArc*i);
			particle.startframe= particles[0].startframe+i*2*fps;
			particle.colours   = particles[0].colours;
		}
	}
}
function animFrame(){
    setInterval(function() {
          animId = requestAnimationFrame(animFrame,canvas);
          onTimer();
    }, 1000/fps);
}

function onTimer(){
	var t1 = new Date().getTime(); 
	dt = 0.001*(t1-t0); 
	t0 = t1;
	if (dt>0.2) {dt=0;};

	if(dt>0){ 
		frame++;
		move()
		}
}
function move(){
	context.clearRect(0, 0, winWidth, winHeight);

	var particles=sporadic.concat(chainfire);
	for (var i=0; i < sporadic.length; i++){
		var obj = particles[i];
		if(obj.startframe <= frame) {
			moveParticle(obj)
			obj.drawLine(context);
			if(obj.age() || obj.x < 0 || obj.x > canvas.width || obj.y < 0 || obj.y > canvas.height) respawn(obj);
		}
	}	
	for (var i=0; i < chainfire.length; i++){ var tmpPart = chainfire[i];
	for (var ii=0; ii < tmpPart.length; ii++){
		var obj = tmpPart[ii];
		if(obj.startframe <= frame) {
			moveParticle(obj)
			obj.drawLine(context);
			if(ii < tmpPart.length-1 && obj.age() || obj.x < 0 || obj.x > canvas.width || obj.y < 0 || obj.y > canvas.height) 
			{respawn(chainfire[i]);}
		}	
	}}

	if(explosion.startframe <= frame) explosion.draw(context, dt)
}
function moveParticle(obj){
	var damping = 1-0.05;
	obj.velo2D = obj.velo2D.subtract(obj.velo2D.multiply(damping*dt));
	obj.lastpos = obj.pos2D;
	obj.pos2D = obj.pos2D.addScaled(obj.velo2D, dt);
}
function Explosion() {
	this.life;
	this.lifespan;
	this.radius;
	this.size;
	this.startframe;
	this.x;
	this.y;
	}
Explosion.prototype.constructor = Explosion;
Explosion.prototype = {
	setup: function(){
		this.life = 0;
		this.lifespan = 3; //0.2+Math.random()*0.8;
		this.radius = 1+Math.random()*Math.random()*6;
		this.size = 80;
		this.startframe = frame+Math.floor(Math.random()*2300);
		this.x = Math.floor(winWidth*Math.random());
		this.y = Math.floor(winHeight*Math.random());
	},
	draw: function(ctx, dt) {
		var factor = 0.1-this.life;
		this.radius += this.radius*factor;
		var grd = ctx.createRadialGradient(this.x, this.y, 0, this.x, this.y, this.radius);
		grd.addColorStop(0.000, "#ffffff00");//grd.addColorStop(0.000, "#ffffff00");
		grd.addColorStop(0.375, "#ffffff02");//grd.addColorStop(0.375, "#ffffff06");
		grd.addColorStop(0.707, "#ffffff04");//grd.addColorStop(0.707, "#ffffff0B");
		grd.addColorStop(0.927, "#ffffff0B");//grd.addColorStop(0.927, "#ffffff11");
		grd.addColorStop(1.000, "#ffffff11");//grd.addColorStop(1.000, "#ffffff19");
		ctx.fillStyle = grd;
		ctx.beginPath();
		ctx.arc(this.x, this.y, this.radius, 0, 2*Math.PI, true);	
		ctx.fill();
		this.life+=dt;
		if(this.life > this.lifespan || this.radius < 1) this.setup();
	}
};
window.onresize = function() {
	canvas.width = winWidth;
	canvas.height = winHeight;
	}
window.onload = init;
</script>
</script>
</body>
