<script>
"use strict"
const
	prt = {radius: 1.27,
		colours: ["#e6dfcb","#d5bb6d","#c2b280","#afa892","#564b29","#cbb676","#564b29","#afa892","#c2b280","#d5bb6d","#e6dfcb"],
		mass: 0.007,
		charge: 1,
		gradient: false	
    },
	fps = 26,
	k = 30,
	windSpeed = 32;

var	winWidth = window.innerWidth,
	winHeight= window.innerHeight,
	winCenW  = winWidth/2,
	winCenH  = winHeight/2,
	winRadius= Math.ceil(Math.sqrt((winCenW/2)**2+(winCenH/2)**2)),
	winCenter= new Vector2D(winCenW, winCenH),
	canvas,
	context,
	animId,
	t0 = 0,dt,
	speed = {
		start:20,
		max:50,
		min: 6
    },
	initVel,
	particles = new Array(),
	maxParticles = Math.floor(winWidth * winHeight/600),
	particleLife = fps*8,
	maxPartPerSec = Math.ceil(maxParticles/particleLife),
	vortices = new Array(),
	numVortices = 3+Math.ceil(Math.random()*6),
	vortexLifetime= 45*fps,
	attractors = new Array(),
	numAttractors = 1+Math.ceil(Math.random()*3),
	attractorsLifetime= 14*fps,
	force,
	wind     = new Vector2D(1,0).multiply(windSpeed),
	windForce,
	windDirection = 0,
	acc;

function createCanvas() {
	canvas  = document.createElement("canvas");
	canvas.width = winWidth;
	canvas.height = winHeight;
	document.body.appendChild(canvas);
	canvas.classList.add("wind");
	return canvas;
}
function init() {
	initVel = new Vector2D(0,1).rotate(Math.random()*Math.PI*2).multiply(speed.start*(1000/fps));
	canvas = createCanvas();
	context = canvas.getContext("2d"); 
	for (var i=0; i< Math.floor(maxParticles/4); i++){ spawn() }
	for (var i=0; i< numVortices; i++){	
		var vortex = new Vortex(winWidth/18, 0.0001, Math.floor(Math.random()*vortexLifetime));	
		vortex.respawn();
		vortex.colours=["purple"];
		vortex.mass = 0.2+Math.random();
		vortex.charge = -3.632	;
		vortex.life = Math.floor(Math.random()*vortexLifetime);
		vortices.push(vortex);
	}	
	for (var i=0; i<numAttractors; i++){	
		var attractor    = new Particle(winWidth * Math.random()/5,["blue","green"],50+Math.random()*30,Math.random()*16,false);	
		attractor.pos2D  = new Vector2D(Math.random()*winWidth,Math.random()*winHeight);
		attractor.velo2D = initVel.multiply(0.01);
		attractors.push(attractor);
	}
	animFrame();
}
function spawn() {
	var particle=new Particle();
	particles.push(particle);
	particle.colours = prt.colours;
	respawn(particle);
	particle.lastcolor = Math.floor(Math.random()*particle.colours.length);
	particle.life = Math.floor(particleLife/2 - Math.random()*0.5*particleLife);
	particle.velo2D = particle.velo2D.multiply(Math.random()*0.5+0.6);
}
function respawn(particle) {
	if(typeof particle=="undefined") {particle=new Particle()}
	particle.x = Math.floor(Math.random()*winWidth);
	particle.y = Math.floor(Math.random()*winHeight);
	particle.life = particleLife;
	particle.velo2D = initVel;
}
function animFrame(){
    setInterval(function() {
          animId = requestAnimationFrame(animFrame,canvas);
          onTimer();
    }, 1000/fps);
}

function onTimer(){
	var t1 = new Date().getTime(); 
	dt = 0.001*(t1-t0); 
	t0 = t1;
	if (dt>0.2) {dt=0;};
	if (dt!=0) {
		windDirection += (Math.random()-0.45)*0.00006;
		wind = wind.rotate(windDirection);
		initVel = wind.multiply(speed.start);
		if(particles.length < maxParticles) {
			for (var i=0; i< maxParticles-particles.length && i < Math.max(Math.ceil(maxPartPerSec*dt), 1); i++){ respawn() }
		}
		move();
	}
}
function move(){
	context.clearRect(0, 0, winWidth, winHeight);
	windForce = wind.multiply(speed.start*dt);
	for (var i=0; i<attractors.length; i++){
		var attractor = attractors[i];
		calcForce(attractor, "attractor");		
		moveAttractor(attractor);
		//attractor.draw(context);
		updateAccel(attractor.mass);
		updateVelo(attractor);	
	}
	for (var i=0; i<vortices.length; i++){
		var vortex=vortices[i];
		calcForce(vortex, "vortex");
		vortex.update(dt);
		//vortex.draw(context);
		updateAccel(vortex.mass);
		updateVelo(vortex);
	}
	for (var i=0; i<particles.length; i++){
		var particle = particles[i];
		particle.life -= dt;
		if(particle.life < 0) {respawn(particle);}
		calcForce(particle, "particle");
		moveParticle(particle);
		particle.draw(context);
		updateAccel(particle.mass);
		updateVelo(particle);		
	}

}
function moveParticle(obj){
	obj.pos2D = obj.pos2D.addScaled(obj.velo2D,dt);	
	if(obj.velo2D.length() < speed.min*dt*5/16) { obj.velo2D =obj.velo2D.multiply(1.01) }
	if(obj.velo2D.length() > speed.max*dt*11/16) { obj.velo2D =obj.velo2D.multiply(0.98) }
	if(obj.vx < -speed.max*dt || obj.vx > speed.max*dt) { obj.vx = obj.vx * 0.63 }//Math.sqrt(obj.vx)}
	if(obj.vy < -speed.max*dt || obj.vy > speed.max*dt) { obj.vy = obj.vy * 0.63 }//Math.sqrt(obj.vy)}
	if(obj.vx > -speed.min*dt || obj.vx < speed.min*dt) { obj.vx = obj.vx * 1.002 }
	if(obj.vy > -speed.min*dt || obj.vy < speed.min*dt) { obj.vy = obj.vy * 1.002 }
	obj.wrap(canvas);
}
function moveAttractor(obj){
	if(obj.velo2D.length > speed.max*dt*0.05) { obj.velo2D.multiply(0.3); }
	obj.pos2D = obj.pos2D.addScaled(obj.velo2D,dt);	
	obj.wrap(canvas);
}
function updateAccel(mass){	acc = force.multiply(1/mass);}	
function updateVelo(obj)  {	obj.velo2D = obj.velo2D.addScaled(acc,dt);}
function calcForce(obj, type){
	force = Forces.zeroForce();
	force = Forces.add([force, windForce.subtract(obj.velo2D.multiply(dt))]);
	switch(type) {
	case "particle":
		calcForceElectric(obj);
		calcForceVortex(obj);
		break;
	case "vortex":
		calcForceElectric(obj);
		break;
	case "attractor":
		calcForceVortex(obj);
		break;
	}
}
function calcForceElectric(obj){	
	var electric;
	for (var i=0; i<numAttractors; i++){
		var attractor = attractors[i];	
		var diff = obj.pos2D.subtract(attractor.pos2D)
		var dist = diff.length();

		if(dist< attractor.radius*4) {
			electric = Forces.electric2(k,attractor.charge,obj.charge,diff);
			force = Forces.add([force, electric]);
		}
	}			
}
function calcForceVortex(obj){	
	var vortex;
	var vForce;
	var electric;
	for (var i=0; i<numVortices; i++){
		vortex = vortices[i];
		var diff = obj.pos2D.subtract(vortex.pos2D)
		var dist = diff.length();
		if(dist< vortex.radius*3) {
			vForce = Forces.vortex(vortex, obj, dt).multiply(dt);
			force = Forces.add([force, vForce]);
		}
		if(dist< vortex.radius*9) {
			if(dist < vortex.radius) {
				electric = Forces.electric2(k,-vortex.charge,obj.charge,diff);
			} else {
				electric = Forces.electric2(k,vortex.charge*3.8,obj.charge,diff);
			};
			force = Forces.add([force, electric]);
		}
	}
}
Vortex.prototype.respawn = function(radius, spinRate, obj=spawnPoint()) {
	if(typeof radius   ==="undefined")   radius = Math.floor(18+Math.random()*winHeight/8);
	if(typeof spinRate ==="undefined") spinRate = Math.sign(Math.random()-0.5)*8-Math.random()*1.8;
	if(typeof obj      ==="undefined")      obj = {};
	if(obj["x"]   ) { this.x    =obj["x"]   } else {this.x       = 0};
	if(obj["y"]   ) { this.y    =obj["y"]   } else {this.y       = 0};
	if(obj["v"]   ) { this.velo2D=obj["v"]  } else {this.this.velo2D = new vector2d(1,1)};
	if(obj["vy"]  ) { this.vy   =obj["vy"]  } else {this.vy      = 0};
	if(obj["life"]) { this.life =obj["life"]} else {this.life    = 0};
	if(obj["lifetime"]){ this.lifetime=obj["lifetime"]} else {this.lifetime= vortexLifetime};
	this.radius_  = radius;
	this.spinRate= spinRate*this.scale;
	if(obj["scale"]){this.scale = obj["scale"]} else {this.scale = defaults.scale};
}
function spawnPoint() {
	var obj = {
		x :Math.random()*winWidth,
		y :Math.random()*winHeight,
		v :wind.multiply(0.6),
		life:Math.ceil(Math.random()*vortexLifetime),
		scale:3.51
	}
	return obj;
}
window.onresize = function() {
	winWidth = canvas.width = window.innerWidth;
	winHeight = canvas.height = window.innerHeight;
	}
window.onload = init;']
</script>
