[h: playerSelect='<option value="all" selected>all</option>']
[h, foreach(pcToken, pcTokens):pcSelect=pcSelect+'<option value="'+pcToken+'">'+pcToken+'</option>']
[h, foreach(player, players):playerSelect=playerSelect+'<option value="'+player+'">'+player+'</option>']
[h: pcSelect=pcSelect+'</select>']
[h: playerSelect=playerSelect+'</select>']


[h, if(devCache), code:{
	[h:'<!-- move CSS & JS here -->']
	[h: css = getLibProperty("controlPanel_css", lib)]
	[h: js = getLibProperty("controlPanel_js", lib)]

	[olObj = json.set('',
	"Lighting",json.append('',"Environment Lighting"),
	"Ground",json.append('',"Drifting Light Rays","Fog","Smoke","Stalag 13"),
	"Fruit",json.append('',"Birds","Bubbles Overhead","Falling Leaves","Firefight","Firefront","Parade","Sandstorm","Swarm"),
	"Rain",json.append('',"Lightning","Rainbow","Rain","Rain Heavy","Rain Overhead","Thunderstorm"),
	"Snow",json.append('',"Blizzard","Snow","Snow2"),
	"Space",json.append('',"Meteor1","Meteor2","Meteor3","Starfield1","Starfield2"),
	"Border", json.append('', "Bloodied","Gradient Edge","Neon Border","Splat")
	)]
	[setLibProperty("controlPanel_olObj", olObj, lib)]

	[soundIDList      = getMacroGroup("Sounds", ",", lib)]
	[soundNameList    = ""]
	[soundMenu        = '<select class="soundmenu" name="sound%{i}" id="sound%{i}"><option value="none">none</option>']
	[foreach(sound, soundIDList), code: {
		[soundName    = getStrProp(getMacroProps(sound, "; ", lib),"label")]
		[soundMenu    = soundMenu + strformat('<option value="%{soundName}">%{soundName}</option>')]
	}]
	[soundMenu = soundMenu + '</select>')]
	[setLibProperty("controlPanel_soundMenu", soundMenu, lib)]

	[layerGroupNames=json.fields(olObj, "json")]
	[layerMenu='<select class="layermenu" id="layer%{i}" name="layer%{i}"><option value="none" selected>none</option>']
	[foreach(layerGroupName, layerGroupNames), code:{
		[layerMenu = layerMenu + '<optgroup label="'+layerGroupName+'">']
		[layerNames=json.get(olObj, layerGroupName)]
		[foreach(layerName, layerNames):
			layerMenu=layerMenu + strformat('<option value="%{layerName}">%{layerName}</option>')]
		[layerMenu = layerMenu + '</optgroup>']
	}]
	[layerMenu = layerMenu + '</select>']	
	[setLibProperty("controlPanel_layerMenu", layerMenu, lib)]


	[h: lightvalues = getLibProperty("lightValues", lib)]
	[h: lightvalueInput = ""]
	[h, foreach(fieldname, json.fields(lightvalues)): lightvalueInput = lightvalueInput + '
	<div class="radiowrapper">
	<label for="layer%{i}_prefs_environmentlighting_'+fieldname+'" class="radiolabel">'+fieldname+'</label>
	<input type="radio" name="layer%{i}_prefs_environmentlighting_lightvalue" 
	id="layer%{i}_prefs_environmentlighting_'+fieldname+'" value="'+json.get(lightvalues, fieldname)+'" '+if(fieldname=="none", "checked", "")+'>
	</div>']
	[setLibProperty("controlPanel_lightvalueInput", lightvalueInput, lib)]
};{
	[olObj = getLibProperty("controlPanel_olObj", lib)]
	[css   = getLibProperty("controlPanel_css", lib)]
	[js    = getLibProperty("controlPanel_js", lib)]
	[layerMenu       = getLibProperty("controlPanel_layerMenu", lib)]
	[soundMenu       = getLibProperty("controlPanel_soundMenu", lib)]
	[lightvalueInput = getLibProperty("controlPanel_lightvalueInput", lib)]
}]

