	[r: '
<html>
<head>
<meta charset="UTF-8">
'+css+'
</head>
<body class="thunder">
  <canvas id="tsCanvas1"></canvas>
  <canvas id="tsCanvas2"></canvas>
  <canvas id="tsCanvas3"></canvas>
</body>
<script type="text/javascript">

var tsCanvas1 = document.getElementById("tsCanvas1");
var tsCanvas2 = document.getElementById("tsCanvas2");
var tsCanvas3 = document.getElementById("tsCanvas3");
var tsCtx1 = tsCanvas1.getContext("2d");
var tsCtx2 = tsCanvas2.getContext("2d");
var tsCtx3 = tsCanvas3.getContext("2d");

var rainthroughnum = 350;
var speedRainTrough = 25;
var RainTrough = [];

var rainnum = 60;
var rain = [];

var lightning = [];
var lightTimeCurrent = 0;
var lightTimeTotal = 0;

var w = tsCanvas1.width = tsCanvas2.width = tsCanvas3.width = window.innerWidth;
var h = tsCanvas1.height = tsCanvas2.height = tsCanvas3.height = window.innerHeight;
window.addEventListener("resize", function() {
  w = tsCanvas1.width = tsCanvas2.width = tsCanvas3.width = window.innerWidth;
  h = tsCanvas1.height = tsCanvas2.height = tsCanvas3.height = window.innerHeight;
});

function random(min, max) {
  return Math.random() * (max - min + 1) + min;
}

function cleartsCanvas1() {
  tsCtx1.clearRect(0, 0, w, h);
}

function cleartsCanvas2() {
  tsCtx2.clearRect(0, 0, tsCanvas2.width, tsCanvas2.height);
}

function cleartsCanvas3() {
  tsCtx3.globalCompositeOperation = "destination-out";
  tsCtx3.fillStyle = "rgba(0,0,0," + random(1, 30) / 100 + ")";
  tsCtx3.fillRect(0, 0, w, h);
  tsCtx3.globalCompositeOperation = "source-over";
};

function createRainTrough() {
  for (var i = 0; i < rainthroughnum; i++) {
    RainTrough[i] = {
      x: random(0, w),
      y: random(0, h),
      length: Math.floor(random(1, 830)),
      opacity: Math.random() * 0.2,
      xs: random(-2, 2),
      ys: random(10, 20)
    };
  }
}

function createRain() {
  for (var i = 0; i < rainnum; i++) {
    rain[i] = {
      x: Math.random() * w,
      y: Math.random() * h,
      l: Math.random() * 1,
      xs: -4 + Math.random() * 4 + 2,
      ys: Math.random() * 10 + 10
    };
  }
}

function createLightning() {
  var x = random(100, w - 100);
  var y = random(0, h / 4);

  var createCount = random(1, 3);
  for (var i = 0; i < createCount; i++) {
    single = {
      x: x,
      y: y,
      xRange: random(5, 30),
      yRange: random(10, 25),
      path: [{
        x: x,
        y: y
      }],
      pathLimit: random(40, 55)
    };
    lightning.push(single);
  }
};

function drawRainTrough(i) {
  tsCtx1.beginPath();
  var grd = tsCtx1.createLinearGradient(0, RainTrough[i].y, 0, RainTrough[i].y + RainTrough[i].length);
  grd.addColorStop(0, "rgba(255,255,255,0)");
  grd.addColorStop(1, "rgba(255,255,255," + RainTrough[i].opacity + ")");

  tsCtx1.fillStyle = grd;
  tsCtx1.fillRect(RainTrough[i].x, RainTrough[i].y, 1, RainTrough[i].length);
  tsCtx1.fill();
}

function drawRain(i) {
  tsCtx2.beginPath();
  tsCtx2.moveTo(rain[i].x, rain[i].y);
  tsCtx2.lineTo(rain[i].x + rain[i].l * rain[i].xs, rain[i].y + rain[i].l * rain[i].ys);
  tsCtx2.strokeStyle = "rgba(174,194,224,0.5)";
  tsCtx2.lineWidth = 1;
  tsCtx2.lineCap = "round";
  tsCtx2.stroke();
}

function drawLightning() {
  for (var i = 0; i < lightning.length; i++) {
    var light = lightning[i];

    light.path.push({
      x: light.path[light.path.length - 1].x + (random(0, light.xRange) - (light.xRange / 2)),
      y: light.path[light.path.length - 1].y + (random(0, light.yRange))
    });

    if (light.path.length > light.pathLimit) {
      lightning.splice(i, 1);
    }

    tsCtx3.strokeStyle = "rgba(255, 255, 255, .1)";
    tsCtx3.lineWidth = 3;
    if (random(0, 15) === 0) {
      tsCtx3.lineWidth = 6;
    }
    if (random(0, 30) === 0) {
      tsCtx3.lineWidth = 8;
    }

    tsCtx3.beginPath();
    tsCtx3.moveTo(light.x, light.y);
    for (var pc = 0; pc < light.path.length; pc++) {
      tsCtx3.lineTo(light.path[pc].x, light.path[pc].y);
    }
    if (Math.floor(random(0, 30)) === 1) { //to fos apo piso
      tsCtx3.fillStyle = "rgba(255, 255, 255, " + random(1, 3) / 100 + ")";
      tsCtx3.fillRect(0, 0, w, h);
    }
    tsCtx3.lineJoin = "miter";
    tsCtx3.stroke();
  }
};

function animateRainTrough() {
	cleartsCanvas1();
	for (var i = 0; i < rainthroughnum; i++) {
		if (RainTrough[i].y >= h) {
			RainTrough[i].y = h - RainTrough[i].y - RainTrough[i].length * 5;
		} else {
			RainTrough[i].y += speedRainTrough;
		}
		drawRainTrough(i);
	}
}

function animateRain() {
	cleartsCanvas2();
	for (var i = 0; i < rainnum; i++) {
		rain[i].x += rain[i].xs;
		rain[i].y += rain[i].ys;
		if (rain[i].x > w || rain[i].y > h) {
			rain[i].x = Math.random() * w;
			rain[i].y = -20;
		}
		drawRain(i);
	}
}

function animateLightning() {
	cleartsCanvas3();
	lightTimeCurrent++;
	if (lightTimeCurrent >= lightTimeTotal) {
		createLightning();
		lightTimeCurrent = 0;
		lightTimeTotal = 200;  //rand(100, 200)
	}
	drawLightning();
}

function init() {
	createRainTrough();
	createRain();
	window.addEventListener("resize", createRainTrough);
}
init();

function animloop() {
	animateRainTrough();
	animateRain();
	animateLightning();
	requestAnimationFrame(animloop);
}
animloop();
</script>
