'+css+'
</style>
</head>
<body>
<canvas id="canvas"></canvas>
<script type="text/javascript">
"use strict"
const fps = 60;
const maxParts = 1000;

var canvas;
var ctx; 
var w, h;
var init = new Array();
var particles = new Array();
var animId, dt, t0;

function draw() {
  ctx.clearRect(0, 0, w, h);
  for (var c = 0; c < particles.length; c++) {
	var p = particles[c];
	ctx.beginPath();
	ctx.moveTo(p.x, p.y);
	ctx.lineTo(p.x + p.l * p.xs, p.y + p.l * p.ys);
	ctx.stroke();
  }
  move();
}

function move() {
  for (var b = 0; b < particles.length; b++) {
	var p = particles[b];
	p.x += p.xs;
	p.y += p.ys;
	if (p.x > w || p.y > h) {
	  p.x = Math.random() * w;
	  p.y = -40;
	}
  }
}
function animFrame(){
    setInterval(function() {
          animId = requestAnimationFrame(animFrame,canvas);
          onTimer();
    }, 1000/fps);
}

function onTimer(){
	var t1 = new Date().getTime(); 
	dt = 0.001*(t1-t0); 
	t0 = t1;
	if (dt>0.2) {dt=0;};
	if(dt>0){ 
		draw()
	}
}

function setup() {
	canvas = document.getElementById("canvas");
	canvas.width = window.innerWidth;
	canvas.height = window.innerHeight;

	ctx = canvas.getContext("2d");
	w = canvas.width;
	h = canvas.height;
	ctx.clearRect(0, 0, w, h);

	for (var a = 0; a < maxParts; a++) {
	  init.push({
		x: Math.random() * w,
		y: Math.random() * h,
		l: Math.random() * 4,
		xs: -4 + Math.random() * 4 ,
		ys: Math.random() * 10 + 18
	  })
	}
	for (var b = 0; b < maxParts; b++) {
	  particles[b] = init[b];
	}

	ctx.strokeStyle = "rgba(174,194,224,0.5)";
	ctx.lineWidth = 1;
	ctx.lineCap = "round";

	canvas.style.display="block";
	animFrame();
}
window.onresize = function() {
	w = window.innerWidth;
	h = window.innerHeight;
	canvas.width = w;
	canvas.height = h;
}
window.onload = setup;
</script>
</body>
</html>
']
