function Ball(radius,color,mass,charge,gradient){
	if(typeof(radius)==="undefined") radius = 20;
	if(typeof(color)==="undefined") color = "#0000ff";
	if(typeof(mass)==="undefined") mass = 1;
	if(typeof(charge)==="undefined") charge = 0;
