}		
Ball.prototype = Object.create(Particle.prototype);
Ball.prototype.constructor = Ball;
Ball.prototype.draw = function (context){
	if (this.gradient){
		grad = context.createRadialGradient(this.x,this.y,0,this.x,this.y,this.radius);
