function Forces(){}
Forces.zeroForce = function() {
	return (new Vector2D(0,0));
}
Forces.electric2 = function(k,q1,q2,r){
	return r.multiply(k*q1*q2/(r.lengthSquared()));
}
Forces.forceField = function(q,E) {
	return E.multiply(q);
