function createNewEmbers(embers) {
	var size = Math.max(minEmberSize, Math.random()*maxEmberSize);
	var ember = new Ball(size,"#88000033",1, 1, true);
    ember.pos2D = new Vector2D(Math.random()*canvasW,Math.random()*canvasH);
