function onTimer(){
	var t1 = new Date().getTime(); 
	dt = 0.001*(t1-t0); 
	t0 = t1;
	if (dt>0.2) {dt=0;};
	forceTheta += Math.random()/10;
	move();
}
function move(){
	context.clearRect(0, 0, canvasW, canvasH);
	forceField = Forces.forceField(Math.random()/10, new Vector2D(Math.cos(forceTheta)*canvasW*2, Math.sin(forceTheta)*canvasH/2)); 
	for (var i=0; i<vortices.length; i++){
