</div>
<script>

"use strict"
var
container = document.getElementById("container"),
cStyle = getComputedStyle(container),
cWidth  = parseInt(cStyle.width ),
cHeight = parseInt(cStyle.height),
descend = {x:0, y:0, z:-0.05},
climb   = {x:0, y:0, z: 0.05},
simulationInfo = {
  boids: [],
  nBoids: parseInt('+nBoidsMultiplier+'*(cWidth+cHeight)/100),
  birdSpeed: '+birdSpeed+',
  avoidanceRange: '+avoidanceRange+',
  alignmentRange: '+alignmentRange+',
  attractionRange: '+attractionRange+',
  avoidanceStrength: '+avoidanceStrength+',
  alignmentStrength: '+alignmentStrength+',
  attractionStrength: '+attractionStrength+',
  handle: 0,
  tick: 0,
};
var birdImgSrc = %{birdSrc};

function getNewBoid() {
  var retVal = {
	position: {x: Math.random(), y: Math.random(), z: Math.random()}, 
	speed: [
		{x: Math.sign(Math.random()-0.5)*0.1+Math.random()*0.9, 
		y: Math.sign(Math.random()-0.5)*0.1+Math.random()*0.9, z: 0}, 
		{x: Math.random()-0.5, y: Math.random()-0.5, z: 0}], 
	highlight: false,
	imgFrame: Math.floor(Math.random()*10),
	element: document.getElementById("birdposO").cloneNode(true)};
  normalize(retVal.speed[0]);
  normalize(retVal.speed[1]);
  retVal.element.style.display="block";
  container.append(retVal.element);
  return retVal;
}

function init() {
  for (var i = 0; i < simulationInfo.nBoids; ++i) {
