context.beginPath();context.arc(this.x,this.y,this.radius,0,2*Math.PI,true);context.closePath();context.fill();},drawLine:function(context){var lineEnd=this.pos2D.add(this.velo2D.multiply(0.051));if(this.gradient){var grad=context.createLinearGradient(this.x,this.y,lineEnd.x,lineEnd.y);grad.addColorStop(0.0,this.colours[0]+"00");grad.addColorStop(0.3,this.colours[0]);grad.addColorStop(0.6,this.colours[this.colours.length-1]+"a1");grad.addColorStop(1.0,this.colours[this.colours.length-1]+"00");context.strokeStyle=grad;}else{this.lastcolor=this.lastcolor==this.colours.length?0:this.lastcolor++;context.strokeStyle=this.colours[this.lastcolor];}
var lineEnd=this.pos2D.add(this.velo2D.multiply(0.03));context.lineWidth=2;context.beginPath();context.moveTo(this.x,this.y);context.lineTo(lineEnd.x,lineEnd.y);context.closePath();context.stroke();},
