}

a {
	text-decoration: none;
}

div {
	margin: 0;
	padding: 0;
}

.lib-menu {
	padding: 0;
	list-style: none;
}

.lib-menu__item {
	display: flex;
	flex-direction: row;
	min-height: " + vLibMenuItemHeight + ";
	margin-bottom: .5rem;
	background: white;
}

.lib-menu__item__img { 
	display: block;
	height: " + vLibMenuItemHeight + ";
}

.lib-menu__item__info {
	flex-grow: 2;
	padding: .5rem;
	padding-bottom: 0;
}

.lib-menu__item__info__libname {
	font-weight: bold;
	font-size: 1.25rem;	
}

.lib-menu__item__updates {
	text-align: right;
	padding: .5rem;
	padding-bottom: 0;
	color:gray;
}

.lib-menu__item__updates--outdated {
	color:red;
}

.lib-menu__item__updates--current {
	color:green;
}

"]

